(function($) {
	
	"use strict";

	jQuery(document).ready(function($) {

	  $('#widgets-right').on('click', '.apb-tab-item', function(event) {
		event.preventDefault();
		var widget = $(this).parents('.widget');
		widget.find('.apb-tab-item').removeClass('active');
		$(this).addClass('active');
		widget.find('.apb-tab').addClass('apb-hide');
		widget.find('.' + $(this).data('toggle')).removeClass('apb-hide');
	  });
		
	  
			
		
		
		var myOptions = {
			// you can declare a default color here,
			// or in the data-default-color attribute on the input
			defaultColor: false,
			// a callback to fire whenever the color changes to a valid color
			change: function(event, ui){
				console.log( event + ' ' + ui );
				
				$(this).parents('.apb-tab').find('select:first').trigger('change');
			},
			// a callback to fire when the input is emptied or an invalid color
			clear: function() {},
			// hide the color picker controls on load
			hide: true,
			// show a group of common colors beneath the square
			// or, supply an array of colors to customize further
			palettes: true
		};

		$('.apb-color-field').wpColorPicker(myOptions);		
		

	});


     	
	
})(jQuery);